        string getStateToken()
        {
            var httpWebRequest = (HttpWebRequest)WebRequest.Create("http://rain.okta1.com:1802/api/v1/authn");
            httpWebRequest.ContentType = "application/json";
            httpWebRequest.Method = "POST";

            using (var streamWriter = new StreamWriter(httpWebRequest.GetRequestStream()))
            {
                string json = "{\"username\":\"administrator1\"," +
                  "\"password\":\"Abcd1234\"}";

                streamWriter.Write(json);
            }

            var httpResponse = (HttpWebResponse)httpWebRequest.GetResponse();
            using (var streamReader = new StreamReader(httpResponse.GetResponseStream()))
            {
                string result = streamReader.ReadToEnd();
                var jss = new JavaScriptSerializer();
                var json = jss.Deserialize<Dictionary<string, dynamic>>(result);
                return json["stateToken"];
            }

            return "dummyStateToken";
